import { useEffect, useState } from 'react';
import { ethers } from 'ethers';

// Components
import Navigation from './components/Navigation';
import Search from './components/Search';
import Home from './components/Home';

// ABIs
import RealEstate from './abis/RealEstate.json'
import Escrow from './abis/Escrow.json'

// Config
import config from './config.json';

function App() {
// ethers is the library our project to the blockchain (ethers provider).
// -> create function for reading and setting the  account to component state. (by default is account doesn't exist return a null value)

  const [account, setAccount] = useState(null) // gives us two different functions (1) read account from the state (2) set account to the state.
  const loadBlockchainData = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum)

// change/update page is you change account inside metamask
    window.ethereum.on('accountsChange', async () => {
      const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
      const account = ethers.utils.getAddress(accounts[0])
      setAccount(account);
    })

  }

// calls loadBlockchainDate() (useEffect hook)
useEffect(() => {
  loadBlockchainData()
}, [])



  return (
    <div>

      <Navigation account={account} setAccount={setAccount} />
      <Search />

      <div className='cards__section'>

        <h3>Explore Homes</h3>

        <hr />

      </div>

    </div>
  );
}

export default App;
